import React from 'react';
import { useLearning } from '../../context/LearningContext';
import feedbackBubbleIcon from '../../assets/images/clarity_talk-bubbles-outline-badged.png';

/**
 * ÌïôÏäµ ÌîºÎìúÎ∞± Ìå®ÎÑê Ïª¥Ìè¨ÎÑåÌä∏
 * ÏÇ¨Ïö©ÏûêÏùò Ïñ∏Ïñ¥ ÌïôÏäµÏóê ÎåÄÌïú ÏûêÎèôÌôîÎêú ÌîºÎìúÎ∞±ÏùÑ ÌëúÏãúÌï©ÎãàÎã§.
 */
const FeedbackPanel: React.FC = () => {
  const { 
    feedback, 
    showFeedbackContent, 
    aiHasResponded, 
    currentStage,
    isPlaying,
    handlePlayAudio
  } = useLearning();

  return (
    <div className="feedback-panel">
      <div className="feedback-title">üì¢ Automated feedback system</div>

      {/* ÌîºÎìúÎ∞± Î∞∞Í≤Ω Ïù¥ÎØ∏ÏßÄ */}
      <div className="feedback-background-image">
        <img src={feedbackBubbleIcon} alt="ÌîºÎìúÎ∞± ÏïÑÏù¥ÏΩò" width="200" height="200" />
      </div>

      {/* ÌîºÎìúÎ∞± Î∞ïÏä§ - AIÍ∞Ä ÏùëÎãµÌïòÍ≥† ÌîºÎìúÎ∞±Ïù¥ ÏûàÏùÑ Îïå ÌëúÏãú */}
      {(aiHasResponded || currentStage === 'feedback') && showFeedbackContent && feedback && (
        <div className="feedback-box">
          <div className="feedback-content">{feedback}</div>

          {/* ÌîºÎìúÎ∞± ÏùåÏÑ± Ïû¨ÏÉù Î≤ÑÌäº */}
          <div 
            className={`play-button ${isPlaying === 'feedback' ? 'playing' : ''}`}
            style={{
              position: 'absolute',
              bottom: '40px',
              right: '20px'
            }}
            onClick={() => handlePlayAudio(feedback, 'feedback')}
          >
            {isPlaying === 'feedback' ? '‚è∏' : '‚ñ∂'}
          </div>
        </div>
      )}
    </div>
  );
};

export default FeedbackPanel;
