import React, { useState } from 'react';
import ChatInterface from './components/ChatInterface';
import FeedbackSystem from './components/FeedbackSystem';
import './styles/globals.css';

// Define message type to ensure proper type checking
type MessageType = 'ai' | 'user';

interface Message {
  type: MessageType;
  text: string;
}

const App: React.FC = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [messages, setMessages] = useState<Message[]>([
    {type: 'ai', text: 'Hello, ICIG! Tell me the situation you want to learn. For example, ordering at a café!'}
  ]);
  const [feedback, setFeedback] = useState<string | null>(null);

  // Function to handle user's voice input
  const handleVoiceInput = (text: string) => {
    const newMessages = [...messages, {type: 'user' as const, text}];
    setMessages(newMessages);
    setCurrentPage(3);

    // Simulate AI response after user input
    setTimeout(() => {
      if (text.includes('first time')) {
        setMessages([...newMessages, {
          type: 'ai' as const, 
          text: '좋아! 그럼 나부터 가볍게 인사해볼게! 안녕~ 나는 ooo야. 만나서 반가워 :) 이제 너 차례야!'
        }]);
        setCurrentPage(4);
      } else if (text.includes('저녁')) {
        setMessages([...newMessages, {
          type: 'ai' as const, 
          text: '좋아! 그럼 나부터 가볍게 인사해볼게! 안녕~ 나는 ooo야. 만나서 반가워 :) 이제 너 차례야!'
        }]);

        // Add feedback for this scenario
        setFeedback(`That's a good sentence! "나는 저녁을 먹었어" means 
"I had dinner."
  You used it well to talk about what you did.
But right now, we're introducing ourselves.
  When you introduce yourself, it's more natural to say your name!

👉 Here's how you can answer:
안녕! 나는 ICIG야. (Hi! I'm ICIG.)
만나서 반가워! 나는 켄이야. (Nice to meet you! I'm Ken.)

📝 Want to try again?
Try saying your name using "나는 ○○야."`);

        setCurrentPage(4);
      }
    }, 1000);
  };

  return (
    <div className="learning-container flex flex-col md:flex-row">
      <div className="logo absolute top-3 left-16 flex items-center">
        <div className="w-16 h-16 relative">
          <div className="absolute top-5 left-1 w-10 h-10 bg-gray-500 border-2 border-gray-500 shadow-md"></div>
          <div className="absolute top-2 left-5 w-10 h-10 bg-gray-400 border-2 border-gray-400 shadow-md"></div>
          <div className="absolute top-0 left-11 w-4 h-4 bg-gray-500 border border-gray-300"></div>
        </div>
        <h1 className="header-title ml-4">Context-aware learning</h1>
      </div>

      <div className="flex-1 md:w-1/2 p-6 pt-32">
        <ChatInterface 
          messages={messages} 
          currentPage={currentPage}
          onVoiceInput={handleVoiceInput}
        />
      </div>

      <div className="flex-1 md:w-1/2 p-6 pt-32">
        <FeedbackSystem 
          feedback={feedback}
          visible={currentPage >= 3}
        />
      </div>
    </div>
  );
};

export default App;
