import React, { useState, useRef, useEffect } from 'react';
import './styles/globals.css';

// Define message type to ensure proper type checking
type MessageType = 'ai' | 'user';

interface Message {
  type: MessageType;
  text: string;
  isVisible?: boolean;
}

interface FeedbackData {
  text: string;
  isVisible: boolean;
}

const App: React.FC = () => {
  // 상태 관리
  const [currentPage, setCurrentPage] = useState(1); // 현재 페이지 상태 (1-4)
  const [isRecording, setIsRecording] = useState(false); // 녹음 상태
  const [messages, setMessages] = useState<Message[]>([
    { type: 'ai', text: 'Hello, ICIG! Tell me the situation you want to learn. For example, ordering at a café!', isVisible: true }
  ]); // 메시지 배열
  const [feedback, setFeedback] = useState<FeedbackData>({
    text: '', 
    isVisible: false
  }); // 피드백 데이터

  // 녹음 관련 상태 및 참조
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);

  // Function to handle text-to-speech
  const handlePlayAudio = (text: string) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = text.match(/[ㄱ-ㅎ|ㅏ-ㅣ|가-힣]/) ? 'ko-KR' : 'en-US';
      window.speechSynthesis.speak(utterance);
    } else {
      console.log('Text-to-speech not supported in this browser');
    }
  };

  // 마이크 버튼 클릭 시 녹음 시작/중지 함수
  const toggleRecording = async () => {
    if (isRecording) {
      // 녹음 중지
      if (mediaRecorderRef.current) {
        mediaRecorderRef.current.stop();
        setIsRecording(false);
      }
    } else {
      try {
        // 마이크 접근 권한 요청
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });

        // 녹음 시작
        const mediaRecorder = new MediaRecorder(stream);
        mediaRecorderRef.current = mediaRecorder;
        audioChunksRef.current = [];

        mediaRecorder.ondataavailable = (event) => {
          if (event.data.size > 0) {
            audioChunksRef.current.push(event.data);
          }
        };

        mediaRecorder.onstop = () => {
          // 녹음 중지 후 처리
          const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });

          // 녹음된 오디오를 처리하고 텍스트로 변환 (실제로는 API 호출)
          simulateSTT(audioBlob);

          // 모든 트랙 중지
          stream.getTracks().forEach(track => track.stop());
        };

        mediaRecorder.start();
        setIsRecording(true);

        // 두번째 페이지로 이동
        setCurrentPage(2);
      } catch (error) {
        console.error('마이크 접근 실패:', error);
      }
    }
  };

  // 음성 텍스트 변환 시뮬레이션 (실제로는 API 호출)
  const simulateSTT = (audioBlob: Blob) => {
    // 실제 구현에서는 여기서 API를 호출하여 음성을 텍스트로 변환
    // 지금은 시뮬레이션으로 임의의 텍스트 반환

    setTimeout(() => {
      // 시뮬레이션 응답
      const userText = currentPage === 2 
        ? "I want to practice talking with someone I'm meeting for the first time." 
        : "나는 저녁을 먹었어.";

      // 사용자 메시지 추가
      const newMessages = [...messages, { type: 'user', text: userText, isVisible: true }];
      setMessages(newMessages);

      // AI 응답 시뮬레이션 (실제로는 API 호출)
      simulateAIResponse(userText, newMessages);
    }, 1000); // 1초 후 응답 시뮬레이션
  };

  // AI 응답 시뮬레이션 (실제로는 API 호출)
  const simulateAIResponse = (userText: string, newMessages: Message[]) => {
    setTimeout(() => {
      if (currentPage === 2) {
        // 첫 번째 사용자 입력 후 AI 응답
        const aiResponse = "좋아! 그럼 나부터 가볍게 인사해볼게! 안녕~ 나는 ooo야. 만나서 반가워 :) 이제 너 차례야!";

        setMessages([...newMessages, { type: 'ai', text: aiResponse, isVisible: true }]);
        setCurrentPage(3);
      } else if (currentPage === 3) {
        // 두 번째 사용자 입력 후 AI 응답 및 피드백
        const aiResponse = "좋아! 그럼 나부터 가볍게 인사해볼게! 안녕~ 나는 ooo야. 만나서 반가워 :) 이제 너 차례야!";

        // 피드백 설정
        setFeedback({
          text: `That's a good sentence! "나는 저녁을 먹었어" means 
"I had dinner."
  You used it well to talk about what you did.
But right now, we're introducing ourselves.
  When you introduce yourself, it's more natural to say your name!

👉 Here's how you can answer:
안녕! 나는 ICIG야. (Hi! I'm ICIG.)
만나서 반가워! 나는 켄이야. (Nice to meet you! I'm Ken.)

📝 Want to try again?
Try saying your name using "나는 ○○야."`,
          isVisible: true
        });

        setMessages([...newMessages, { type: 'ai', text: aiResponse, isVisible: true }]);
        setCurrentPage(4);
      }
    }, 1000); // 1초 후 응답 시뮬레이션
  };

  return (
    <div className="v1_248">
      {/* Logo */}
      <div className="v1_249">
        <div className="v1_250"></div>
        <div className="v1_251"></div>
        <div className="v1_252"></div>
        <div className="v1_253"></div>
      </div>

      {/* Title */}
      <div className="v1_254">
        <span className="v1_255">Context-aware learning</span>
      </div>

      {/* Feedback panel */}
      <div className="v1_256"></div>

      {/* Chat bubbles */}
      <div className="v1_257"></div>
      <div className="v1_258"></div>
      <div className="v1_259"></div>
      <div className="v1_260"></div>

      {/* Chat text */}
      <span className="v1_261">Hello, ICIG! Tell me the situation you want to learn. For example, ordering at a café!</span>
      <span className="v1_262">좋아! 그럼 나부터 가볍게 인사해볼게! 안녕~ 나는 ooo야. 만나서 반가워 :) 이제 너 차례야!</span>
      <span className="v1_263">I want to practice talking with someone I'm meeting for the first time.</span>
      <span className="v1_264">나는 저녁을 먹었어.</span>

      <div className="v1_265"></div>

      {/* AI Icon for first message */}
      <div className="v1_266">
        <div className="v1_267"></div>
        <div className="v1_268"></div>
        <div className="v1_269"></div>
        <div className="v1_270"></div>
      </div>

      {/* AI Icon for third message */}
      <div className="v1_271">
        <div className="v1_272"></div>
        <div className="v1_273"></div>
        <div className="v1_274"></div>
        <div className="v1_275"></div>
