import React, { useState, useRef, useEffect } from 'react';
import './styles/globals.css';

// Define message type to ensure proper type checking
type MessageType = 'ai' | 'user';

interface Message {
  type: MessageType;
  text: string;
  isVisible?: boolean;
}

interface FeedbackData {
  text: string;
  isVisible: boolean;
}

const App: React.FC = () => {
  // ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [currentPage, setCurrentPage] = useState(1); // ÌòÑÏû¨ ÌéòÏù¥ÏßÄ ÏÉÅÌÉú (1-4)
  const [isRecording, setIsRecording] = useState(false); // ÎÖπÏùå ÏÉÅÌÉú
  const [messages, setMessages] = useState<Message[]>([
    { type: 'ai', text: 'Hello, ICIG! Tell me the situation you want to learn. For example, ordering at a caf√©!', isVisible: true }
  ]); // Î©îÏãúÏßÄ Î∞∞Ïó¥
  const [feedback, setFeedback] = useState<FeedbackData>({
    text: '', 
    isVisible: false
  }); // ÌîºÎìúÎ∞± Îç∞Ïù¥ÌÑ∞

  // ÎÖπÏùå Í¥ÄÎ†® ÏÉÅÌÉú Î∞è Ï∞∏Ï°∞
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);

  // Function to handle text-to-speech
  const handlePlayAudio = (text: string) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = text.match(/[„Ñ±-„Öé|„Öè-„Ö£|Í∞Ä-Ìû£]/) ? 'ko-KR' : 'en-US';
      window.speechSynthesis.speak(utterance);
    } else {
      console.log('Text-to-speech not supported in this browser');
    }
  };

  // ÎßàÏù¥ÌÅ¨ Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú ÎÖπÏùå ÏãúÏûë/Ï§ëÏßÄ Ìï®Ïàò
  const toggleRecording = async () => {
    if (isRecording) {
      // ÎÖπÏùå Ï§ëÏßÄ
      if (mediaRecorderRef.current) {
        mediaRecorderRef.current.stop();
        setIsRecording(false);
      }
    } else {
      try {
        // ÎßàÏù¥ÌÅ¨ Ï†ëÍ∑º Í∂åÌïú ÏöîÏ≤≠
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });

        // ÎÖπÏùå ÏãúÏûë
        const mediaRecorder = new MediaRecorder(stream);
        mediaRecorderRef.current = mediaRecorder;
        audioChunksRef.current = [];

        mediaRecorder.ondataavailable = (event) => {
          if (event.data.size > 0) {
            audioChunksRef.current.push(event.data);
          }
        };

        mediaRecorder.onstop = () => {
          // ÎÖπÏùå Ï§ëÏßÄ ÌõÑ Ï≤òÎ¶¨
          const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });

          // ÎÖπÏùåÎêú Ïò§ÎîîÏò§Î•º Ï≤òÎ¶¨ÌïòÍ≥† ÌÖçÏä§Ìä∏Î°ú Î≥ÄÌôò (Ïã§Ï†úÎ°úÎäî API Ìò∏Ï∂ú)
          simulateSTT(audioBlob);

          // Î™®Îì† Ìä∏Îûô Ï§ëÏßÄ
          stream.getTracks().forEach(track => track.stop());
        };

        mediaRecorder.start();
        setIsRecording(true);

        // ÎëêÎ≤àÏß∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        setCurrentPage(2);
      } catch (error) {
        console.error('ÎßàÏù¥ÌÅ¨ Ï†ëÍ∑º Ïã§Ìå®:', error);
      }
    }
  };

  // ÏùåÏÑ± ÌÖçÏä§Ìä∏ Î≥ÄÌôò ÏãúÎÆ¨Î†àÏù¥ÏÖò (Ïã§Ï†úÎ°úÎäî API Ìò∏Ï∂ú)
  const simulateSTT = (audioBlob: Blob) => {
    // Ïã§Ï†ú Íµ¨ÌòÑÏóêÏÑúÎäî Ïó¨Í∏∞ÏÑú APIÎ•º Ìò∏Ï∂úÌïòÏó¨ ÏùåÏÑ±ÏùÑ ÌÖçÏä§Ìä∏Î°ú Î≥ÄÌôò
    // ÏßÄÍ∏àÏùÄ ÏãúÎÆ¨Î†àÏù¥ÏÖòÏúºÎ°ú ÏûÑÏùòÏùò ÌÖçÏä§Ìä∏ Î∞òÌôò

    setTimeout(() => {
      // ÏãúÎÆ¨Î†àÏù¥ÏÖò ÏùëÎãµ
      const userText = currentPage === 2 
        ? "I want to practice talking with someone I'm meeting for the first time." 
        : "ÎÇòÎäî Ï†ÄÎÖÅÏùÑ Î®πÏóàÏñ¥.";

      // ÏÇ¨Ïö©Ïûê Î©îÏãúÏßÄ Ï∂îÍ∞Ä
      const newMessages = [...messages, { type: 'user', text: userText, isVisible: true }];
      setMessages(newMessages);

      // AI ÏùëÎãµ ÏãúÎÆ¨Î†àÏù¥ÏÖò (Ïã§Ï†úÎ°úÎäî API Ìò∏Ï∂ú)
      simulateAIResponse(userText, newMessages);
    }, 1000); // 1Ï¥à ÌõÑ ÏùëÎãµ ÏãúÎÆ¨Î†àÏù¥ÏÖò
  };

  // AI ÏùëÎãµ ÏãúÎÆ¨Î†àÏù¥ÏÖò (Ïã§Ï†úÎ°úÎäî API Ìò∏Ï∂ú)
  const simulateAIResponse = (userText: string, newMessages: Message[]) => {
    setTimeout(() => {
      if (currentPage === 2) {
        // Ï≤´ Î≤àÏß∏ ÏÇ¨Ïö©Ïûê ÏûÖÎ†• ÌõÑ AI ÏùëÎãµ
        const aiResponse = "Ï¢ãÏïÑ! Í∑∏Îüº ÎÇòÎ∂ÄÌÑ∞ Í∞ÄÎ≥çÍ≤å Ïù∏ÏÇ¨Ìï¥Î≥ºÍ≤å! ÏïàÎÖï~ ÎÇòÎäî oooÏïº. ÎßåÎÇòÏÑú Î∞òÍ∞ÄÏõå :) Ïù¥Ï†ú ÎÑà Ï∞®Î°ÄÏïº!";

        setMessages([...newMessages, { type: 'ai', text: aiResponse, isVisible: true }]);
        setCurrentPage(3);
