import React, { useState, useRef, useEffect } from 'react';
import { AudioRecorder } from './utils/audioUtils';
import './styles/globals.css';
import './styles/learningInterface.css';

// Ïù¥ÎØ∏ÏßÄ ÏûÑÌè¨Ìä∏
import aiChatIcon from './images/alstj1.png';
import userChatIcon from './images/Vector.png';
import microphoneIcon from './images/mdi-light_microphone.svg';
import feedbackBubbleIcon from './images/clarity_talk-bubbles-outline-badged.png';
import logoIcon from './images/clarity_talk-bubbles-outline-badged.svg';
import arrowIcon from './images/clarity_arrow-line.svg';

// Define message type to ensure proper type checking
type MessageType = 'ai' | 'user';

interface Message {
  type: MessageType;
  text: string;
  id: string;
}

// ÌïôÏäµ ÏÉÅÌÉúÎ•º Ï∂îÏ†ÅÌïòÍ∏∞ ÏúÑÌïú ÌÉÄÏûÖ
type LearningStage = 'initial' | 'userInput' | 'aiResponse' | 'feedback';

const App: React.FC = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [currentStage, setCurrentStage] = useState<LearningStage>('initial');
  const [messages, setMessages] = useState<Message[]>([
    { 
      type: 'ai', 
      text: 'Hello, ICIG! Tell me the situation you want to learn. For example, ordering at a caf√©!',
      id: 'initial-message'
    }
  ]);
  const [feedback, setFeedback] = useState<string>('');
  const [showFeedbackContent, setShowFeedbackContent] = useState(false);
  const [aiHasResponded, setAiHasResponded] = useState(false);
  const [audioURL, setAudioURL] = useState<string | null>(null);
  const [isPlaying, setIsPlaying] = useState<string | null>(null); // ÌòÑÏû¨ Ïû¨ÏÉùÏ§ëÏù∏ Ïò§ÎîîÏò§ ID

  // AudioRecorder Ïù∏Ïä§ÌÑ¥Ïä§ Ï†ÄÏû•
  const recorderRef = useRef<AudioRecorder | null>(null);

  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú AudioRecorder Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    recorderRef.current = new AudioRecorder();

    return () => {
      // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ïò§ÎîîÏò§ URL Ï†ïÎ¶¨
      if (audioURL) {
        AudioRecorder.revokeAudioURL(audioURL);
      }
    };
  }, [audioURL]);

  // ÌòÑÏû¨ Ïû¨ÏÉù Ï§ëÏù∏ utterance Ï∞∏Ï°∞
  const utteranceRef = useRef<SpeechSynthesisUtterance | null>(null);

  // ÌÖçÏä§Ìä∏Î•º ÏùåÏÑ±ÏúºÎ°ú Î≥ÄÌôò
  const handlePlayAudio = (text: string, id: string) => {
    if ('speechSynthesis' in window) {
      // ÌòÑÏû¨ ÌÅ¥Î¶≠Ìïú Í≤ÉÍ≥º ÎèôÏùºÌïú Î≤ÑÌäºÏù¥ Ïû¨ÏÉù Ï§ëÏù¥Î©¥ (Ï§ëÏßÄ Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú)
      if (isPlaying === id) {
        window.speechSynthesis.cancel(); // ÌòÑÏû¨ Ïû¨ÏÉù Ï§ëÏù∏ Ïò§ÎîîÏò§ Ï§ëÏßÄ
        setIsPlaying(null); // Ïû¨ÏÉù ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
        return; // Ìï®Ïàò Ï¢ÖÎ£å
      }

      // Îã§Î•∏ Î≤ÑÌäºÏù¥ Ïû¨ÏÉù Ï§ëÏù¥Î©¥ Ìï¥Îãπ Ïò§ÎîîÏò§ Ï§ëÏßÄ
      if (isPlaying) {
        window.speechSynthesis.cancel();
      }

      // ÏÉà utterance ÏÉùÏÑ±
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = text.match(/[„Ñ±-„Ö£|Í∞Ä-Ìû£]/) ? 'ko-KR' : 'en-US';

      // Ï∞∏Ï°∞ Ï†ÄÏû•
      utteranceRef.current = utterance;

      // Ïû¨ÏÉù ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
      setIsPlaying(id);

      // Ïû¨ÏÉù ÏôÑÎ£å Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
