import React, { useState, useRef, useEffect } from 'react';
import { AudioRecorder } from './utils/audioUtils';
import './styles/globals.css';
import './styles/learningInterface.css';

// Define message type to ensure proper type checking
type MessageType = 'ai' | 'user';

interface Message {
  type: MessageType;
  text: string;
  id: string;
}

// ÌïôÏäµ ÏÉÅÌÉúÎ•º Ï∂îÏ†ÅÌïòÍ∏∞ ÏúÑÌïú ÌÉÄÏûÖ
type LearningStage = 'initial' | 'userInput' | 'aiResponse' | 'feedback';

const App: React.FC = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [currentStage, setCurrentStage] = useState<LearningStage>('initial');
  const [messages, setMessages] = useState<Message[]>([
    { 
      type: 'ai', 
      text: 'Hello, ICIG! Tell me the situation you want to learn. For example, ordering at a caf√©!',
      id: 'initial-message'
    }
  ]);
  const [feedback, setFeedback] = useState<string | null>(null);
  const [audioURL, setAudioURL] = useState<string | null>(null);
  const [isPlaying, setIsPlaying] = useState<string | null>(null); // ÌòÑÏû¨ Ïû¨ÏÉùÏ§ëÏù∏ Ïò§ÎîîÏò§ ID

  // AudioRecorder Ïù∏Ïä§ÌÑ¥Ïä§ Ï†ÄÏû•
  const recorderRef = useRef<AudioRecorder | null>(null);

  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú AudioRecorder Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    recorderRef.current = new AudioRecorder();

    return () => {
      // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ïò§ÎîîÏò§ URL Ï†ïÎ¶¨
      if (audioURL) {
        AudioRecorder.revokeAudioURL(audioURL);
      }
    };
  }, [audioURL]);

  // ÌÖçÏä§Ìä∏Î•º ÏùåÏÑ±ÏúºÎ°ú Î≥ÄÌôò
  const handlePlayAudio = (text: string, id: string) => {
    if ('speechSynthesis' in window) {
      // Ïù¥ÎØ∏ Ïû¨ÏÉù Ï§ëÏù∏ Ïò§ÎîîÏò§Í∞Ä ÏûàÏúºÎ©¥ Ï§ëÏßÄ
      if (isPlaying) {
        window.speechSynthesis.cancel();
      }

      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = text.match(/[„Ñ±-„Öé|„Öè-„Ö£|Í∞Ä-Ìû£]/) ? 'ko-KR' : 'en-US';

      // Ïû¨ÏÉù ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
      setIsPlaying(id);

      // Ïû¨ÏÉù ÏôÑÎ£å Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
      utterance.onend = () => {
        setIsPlaying(null);
      };

      window.speechSynthesis.speak(utterance);
    } else {
      console.log('Text-to-speech not supported in this browser');
    }
  };

  // Í≥†Ïú† ID ÏÉùÏÑ± Ìï®Ïàò
  const generateId = () => {
    return Math.random().toString(36).substring(2, 15);
  };

  // ÎÖπÏùå ÏãúÏûë/Ï§ëÏßÄ Ìï®Ïàò
  const toggleRecording = async () => {
    if (isRecording) {
      try {
        // ÎÖπÏùå Ï§ëÏßÄ
        setIsRecording(false);

        if (recorderRef.current) {
          const audioBlob = await recorderRef.current.stopRecording();
          const url = AudioRecorder.createAudioURL(audioBlob);
          setAudioURL(url);

          // Ïó¨Í∏∞ÏÑúÎäî ÏÇ¨Ïö©ÏûêÍ∞Ä Ïã§Ï†úÎ°ú ÎßêÌïú ÌÖçÏä§Ìä∏Î•º ÏñªÍ∏∞ ÏúÑÌï¥ ÏùåÏÑ±-ÌÖçÏä§Ìä∏ APIÎ•º Ìò∏Ï∂úÌï† Í≤ÉÏûÖÎãàÎã§.
          // ÏòàÏ†ú Î™©Ï†ÅÏúºÎ°ú ÏÇ¨Ïö©Ïûê ÏûÖÎ†• ÏãúÎÆ¨Î†àÏù¥ÏÖò
          simulateUserInput();
        }
      } catch (error) {
        console.error('ÎÖπÏùå Ï§ëÏßÄ Ïò§Î•ò:', error);
        setIsRecording(false);
      }
    } else {
      try {
        // ÎÖπÏùå ÏãúÏûë
        if (recorderRef.current) {
          await recorderRef.current.startRecording();
          setIsRecording(true);
        }
      } catch (error) {
        console.error('ÎÖπÏùå ÏãúÏûë Ïò§Î•ò:', error);
      }
    }
  };

  // ÏÇ¨Ïö©Ïûê ÏûÖÎ†• ÏãúÎÆ¨Î†àÏù¥ÏÖò
  const simulateUserInput = () => {
    // ÏÇ¨Ïö©ÏûêÍ∞Ä ÎßêÌïú ÏòàÏãú ÌÖçÏä§Ìä∏ (Ïã§Ï†úÎ°úÎäî ÏùåÏÑ±-ÌÖçÏä§Ìä∏ APIÏóêÏÑú ÏñªÏñ¥Ïò¥)
    const userInput = currentStage === 'initial' ? 
      "I want to practice talking with someone I'm meeting for the first time." : 
      "ÎÇòÎäî Ï†ÄÎÖÅÏùÑ Î®πÏóàÏñ¥.";

    // Î©îÏãúÏßÄ Î™©Î°ùÏóê ÏÇ¨Ïö©Ïûê ÏûÖÎ†• Ï∂îÍ∞Ä (id Ï∂îÍ∞Ä)
    const userMessageId = generateId();
    const updatedMessages = [...messages, { 
      type: 'user' as const, 
      text: userInput,
      id: userMessageId
    }];

    setMessages(updatedMessages);
    setCurrentStage('userInput');

    // AI ÏùëÎãµ ÏãúÎÆ¨Î†àÏù¥ÏÖò (ÏßßÏùÄ ÏßÄÏó∞ ÌõÑ)
    setTimeout(() => {
      simulateAIResponse(updatedMessages, userInput);
    }, 1000);
  };

  // AI ÏùëÎãµ ÏãúÎÆ¨Î†àÏù¥ÏÖò
  const simulateAIResponse = (currentMessages: Message[], userInput: string) => {
    let aiResponse = '';
    let newFeedback = null;

    if (userInput.includes('first time')) {
      aiResponse = 'Ï¢ãÏïÑ! Í∑∏Îüº ÎÇòÎ∂ÄÌÑ∞ Í∞ÄÎ≥çÍ≤å Ïù∏ÏÇ¨Ìï¥Î≥ºÍ≤å! ÏïàÎÖï~ ÎÇòÎäî oooÏïº. ÎßåÎÇòÏÑú Î∞òÍ∞ÄÏõå :) Ïù¥Ï†ú ÎÑà Ï∞®Î°ÄÏïº!';
      setCurrentStage('aiResponse');
    } else if (userInput.includes('Ï†ÄÎÖÅ')) {
      aiResponse = 'Ï¢ãÏïÑ! Í∑∏Îüº ÎÇòÎ∂ÄÌÑ∞ Í∞ÄÎ≥çÍ≤å Ïù∏ÏÇ¨Ìï¥Î≥ºÍ≤å! ÏïàÎÖï~ ÎÇòÎäî oooÏïº. ÎßåÎÇòÏÑú Î∞òÍ∞ÄÏõå :) Ïù¥Ï†ú ÎÑà Ï∞®Î°ÄÏïº!';

      // ÌîºÎìúÎ∞± Ï∂îÍ∞Ä
      newFeedback = `That's a good sentence! "ÎÇòÎäî Ï†ÄÎÖÅÏùÑ Î®πÏóàÏñ¥" means 
"I had dinner."
  You used it well to talk about what you did.
But right now, we're introducing ourselves.
  When you introduce yourself, it's more natural to say your name!

